import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, Image, TouchableOpacity, Keyboard } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { useRef } from 'react';
import { Animated, Easing } from 'react-native';
import { Circle, Svg } from 'react-native-svg';

import characterData from './data/characters.json';
import CharacterDetailScreen from './screens/CharacterDetailScreen.js';
import SearchBar from './components/SearchBar.js';

import TravelerImage from './images/Traveler.jpg';
import VentiImage from './images/Venti.jpg';
import DilucImage from './images/Diluc.jpg';
// import other character images as needed

function CharacterListScreen() {
  const navigation = useNavigation();
  const [search, setSearch] = useState('');
  const [filteredCharacterData, setFilteredCharacterData] = useState([]);

  useEffect(() => {
    setFilteredCharacterData(
      characterData.filter((character) =>
        character.name.toLowerCase().includes(search.toLowerCase())
      )
    );
  }, [search]);

  const handleCharacterPress = (character) => {
    navigation.navigate('CharacterDetail', { character });
  };

  return (
    <View>
      <SearchBar value={search} onChangeText={setSearch} />
      <FlatList
        data={filteredCharacterData}
        keyExtractor={(character) => character.name}
        renderItem={({ item }) => (
          <TouchableOpacity
            onPress={() => handleCharacterPress(item)}
            style={styles.characterContainer}
          >
            <Image source={getImageForCharacter(item)} style={styles.characterImage} />
            <Text style={styles.characterName}>{item.name}</Text>
          </TouchableOpacity>
        )}
      />
    </View>
  );
}

function getImageForCharacter(character) {
  switch (character.name) {
    case 'Traveler':
      return TravelerImage;
    case 'Venti':
      return VentiImage;
    case 'Diluc':
      return DilucImage;
    // add other cases for other characters as needed
    default:
      return null;
  }
}

const AppOpeningAnimation = () => {
  const animatedValue = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    startAnimation();
  }, []);

  const startAnimation = () => {
    Animated.timing(animatedValue, {
      toValue: 1,
      duration: 2000,
      easing: Easing.linear,
      useNativeDriver: true,
    }).start();
  };

  const progress = animatedValue.interpolate({
    inputRange: [0, 1],
    outputRange: ['0%', '100%'],
  });

  return (
    <Svg width="100%" height="100%" viewBox="0 0 100 100">
      <Circle
        cx="50"
        cy="50"
        r="45"
        stroke="#ffffff"
        strokeWidth="10"
        fill="transparent"
      />
      <Circle
        cx="50"
        cy="50"
        r="45"
        stroke="#ffffff"
        strokeWidth="10"
        fill="transparent"
        strokeDasharray="285"
        strokeDashoffset={animatedValue.interpolate({
          inputRange: [0, 1],
          outputRange: [285, 0],
        })}
      />
      <Text
        x="50%"
        y="50%"
        textAnchor="middle"
        dominantBaseline="central"
        fill="#ffffff"
        fontSize={20}
        fontWeight="bold"
      >
        {progress}
      </Text>
    </Svg>
  );
};


const styles = StyleSheet.create({
  characterContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingVertical: 8,
    paddingHorizontal: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  characterImage: {
    width: 48,
    height: 48,
    marginRight: 16,
  },
  characterName: {
    fontSize: 18,
    fontWeight: 'bold',
  },
});

const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="AppOpeningAnimation" component={AppOpeningAnimation} />
        <Stack.Screen name="CharacterList" component={CharacterListScreen} />
        <Stack.Screen name="CharacterDetail" component={CharacterDetailScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
